import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import listPokemon from "../models/listPokemon";

export default function DetailPokemon({ id }) {
  const [pokemon, setPokemon] = useState(null);

  useEffect(() => {
    listPokemon.forEach((pokemon) => {
      if (id.params.id === pokemon.id.toString()) {
        setPokemon(pokemon);
      }
    });
  }, [id.params.id]);

  function handleItem(id) {
    console.log("ok");
  }

  return (
    <div>
      {pokemon ? (
        <div className="row" onClick={() => handleItem()}>
          <div className="col s12 m8 offset-m2">
            <h2 className="header center">{pokemon.name}</h2>
            <div className="card hoverable">
              <div className="card-image">
                <img
                  src={pokemon.picture}
                  alt={pokemon.name}
                  style={{ width: "250px", margin: "0 auto" }}
                />
              </div>
              <div className="card-stacked">
                <div className="card-content">
                  <table className="bordered striped">
                    <tbody>
                      <tr>
                        <td>Nom</td>
                        <td>
                          <strong>{pokemon.name}</strong>
                        </td>
                      </tr>
                      <tr>
                        <td>Points de vie</td>
                        <td>
                          <strong>{pokemon.hp}</strong>
                        </td>
                      </tr>
                      <tr>
                        <td>Dégâts</td>
                        <td>
                          <strong>{pokemon.cp}</strong>
                        </td>
                      </tr>
                      {/* <tr>
                        <td>Types</td>
                        <td>
                          {pokemon.types.map((type) => (
                            <span key={type} className={formatType(type)}>
                              {type}
                            </span>
                          ))}
                        </td>
                      </tr>
                      <tr>
                        <td>Date de création</td>
                        <td>{formatDate(pokemon.created)}</td>
                      </tr> */}
                    </tbody>
                  </table>
                </div>
                <div className="card-action">
                  <Link to="/">Retour</Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        <h4 className="center">Aucun pokémon à afficher !</h4>
      )}
    </div>
  );
}
